"use client"
import React, { useState } from 'react';

const Stories = () => {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedGenre, setSelectedGenre] = useState('');
  const [selectedStory, setSelectedStory] = useState<Story | null>(null);

  const genres = ['Greek', 'Roman', 'Norse', 'Egyptian', 'Celtic', 'Japanese', 'Hindu', 'Mesopotamian'];

  const stories = [
    {
      id: 1,
      title: 'The Odyssey',
      description: "A tale of Odysseus's long journey home.",
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuA_Q7FRu2muA1GVM0nuxEZ9uHhXcqYVaYWEHnHz-FestszrVtOfhweEdYyWTIncxEpzBBTuOKABJOq-9ncs3e-aNHUv9933r5Iu1ve6ns--UkmIm3GUJE3Np_McB00nHQIH4gXEouW63YDlMt7KfFln2d2vQfT_oN1RI6eDX1asZuo5Oay1hgubfG8YnHeIoXqGSPEYWeo7VybUA2voq2b2sqG6eq09AUWsJzo9akQMM8Y9PQ1TC4qEKAstlG1RhVyC2_n6dnaMBJqM',
      genre: 'Greek',
      story: "After the fall of Troy, Odysseus begins his perilous journey home to Ithaca. He faces the wrath of Poseidon and encounters monsters, gods, and temptations that test his cunning and resolve."
    },
    {
      id: 2,
      title: 'The Aeneid',
      description: "The epic of Aeneas's journey to found Rome.",
      image: 'data:image/jpeg;base64,',
      genre: 'Roman',
      story: "Aeneas flees burning Troy with his family and followers, destined by the gods to establish a new homeland in Italy. His journey becomes the foundation myth of Rome."
    },
    {
      id: 3,
      title: "Thor's Hammer",
      description: "Thor's quest to retrieve his stolen hammer.",
      image: 'data:image/jpeg;base64,',
      genre: 'Norse',
      story: "When Thor awakens to find his mighty hammer Mjolnir stolen, he must disguise himself as a bride to infiltrate the giant's wedding and reclaim his weapon."
    },
    {
      id: 4,
      title: 'The Book of the Dead',
      description: 'Ancient Egyptian funerary texts.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuCsp4WC_hw7JP4K2y_VHRpjY8_H0gZNTEk-GMt_cSK1LPTSbIVXhv-iyMAjymQQ--Hl4RAJuOD1gnwE-TBSPWP5cHnj9Z9e7--1MQKAPTWQfxL1EcCOp558sw2WUGhrQ8tsLtPwUevQrH0loaIVpiTQCbHLMNuXViM2RAZqQKiSzb8xDS95RLYRCndil70NdQp2jhVSVxdFelw4ucdWUX3SREgnl99CoSzpbSwWWKC9nX3pHwAOJdRyo9F23xQ0wmtjVYPSzUnKxmsh',
      genre: 'Egyptian',
      story: "Ancient spells and prayers guide the deceased through the perilous journey of the afterlife. The soul must pass judgment before Osiris to achieve eternal life."
    },
    {
      id: 5,
      title: 'The Mabinogion',
      description: 'A collection of Welsh tales.',
      image: 'data:image/jpeg;base64,',
      genre: 'Celtic',
      story: "Four branches of ancient Welsh mythology tell of magical transformations, love, betrayal, and the eternal struggle between the mortal and otherworldly realms."
    },
    {
      id: 6,
      title: 'Perseus and Medusa',
      description: 'The hero who slayed the Gorgon.',
      image: 'data:image/jpeg;base64,',
      genre: 'Greek',
      story: "Armed with a mirrored shield and winged sandals, Perseus ventures to slay Medusa whose gaze turns all who look upon her to stone."
    },
    {
      id: 7,
      title: 'Pandora\'s Box',
      description: 'The first woman opens forbidden knowledge.',
      image: 'data:image/jpeg;base64,',
      genre: 'Greek',
      story: "Curious Pandora opens the jar given by the gods, releasing all evils into the world but leaving hope trapped within."
    },
    {
      id: 8,
      title: 'Ragnarök',
      description: 'The twilight of the Norse gods.',
      image: 'data:image/jpeg;base64,',
      genre: 'Norse',
      story: "The final battle approaches as Fenrir the wolf breaks free and the gods prepare for their ultimate fate in the end of all things."
    },
    {
      id: 9,
      title: 'Amaterasu\'s Cave',
      description: 'The sun goddess hides from her brother.',
      image: 'data:image/jpeg;base64,',
      genre: 'Japanese',
      story: "When Susanoo's rage drives Amaterasu into a cave, the world plunges into darkness until the other gods devise a clever plan to coax her out."
    },
    {
      id: 10,
      title: 'The Ramayana',
      description: 'Prince Rama\'s quest to rescue Sita.',
      image: 'data:image/jpeg;base64,',
      genre: 'Hindu',
      story: "When demon king Ravana abducts Sita, Prince Rama embarks on an epic journey with his loyal companion Hanuman to rescue his beloved wife."
    },
    {
      id: 11,
      title: 'The Epic of Gilgamesh',
      description: 'The first hero\'s journey for immortality.',
      image: 'data:image/jpeg;base64,',
      genre: 'Mesopotamian',
      story: "King Gilgamesh embarks on a quest for eternal life after the death of his dear friend Enkidu, learning hard truths about mortality."
    },
    {
      id: 12,
      title: 'Osiris and Set',
      description: 'Brothers divided by jealousy and death.',
      image: 'data:image/jpeg;base64,',
      genre: 'Egyptian',
      story: "Set's jealousy leads him to murder and dismember his brother Osiris, but Isis's love and magic bring about resurrection and justice."
    },
    {
      id: 13,
      title: 'Romulus and Remus',
      description: 'Twin brothers found Rome.',
      image: 'data:image/jpeg;base64,',
      genre: 'Roman',
      story: "Raised by a she-wolf, the twin brothers grow up to found a great city, but their rivalry ends in tragedy with only Romulus surviving to rule Rome."
    },
    {
      id: 14,
      title: 'Loki\'s Binding',
      description: 'The trickster god faces his punishment.',
      image: 'data:image/jpeg;base64,',
      genre: 'Norse',
      story: "After causing Baldr's death, Loki is bound beneath the earth with serpent's venom dripping on his face until Ragnarök arrives."
    },
    {
      id: 15,
      title: 'The Táin',
      description: 'The cattle raid of Cooley.',
      image: 'data:image/jpeg;base64,',
      genre: 'Celtic',
      story: "Queen Medb's desire for the Brown Bull of Cooley sparks an epic war, with young hero Cú Chulainn standing alone against her army."
    }
  ];

  const [currentPage, setCurrentPage] = useState(1);
  const storiesPerPage = 8;

  const filteredStories = stories.filter(story => {
    const matchesSearch = story.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
                         story.description.toLowerCase().includes(searchQuery.toLowerCase());
    const matchesGenre = !selectedGenre || story.genre === selectedGenre;
    return matchesSearch && matchesGenre;
  });

  const totalPages = Math.ceil(filteredStories.length / storiesPerPage);
  const startIndex = (currentPage - 1) * storiesPerPage;
  const displayedStories = filteredStories.slice(startIndex, startIndex + storiesPerPage);

  const MagnifyingGlassIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
      <path d="M229.66,218.34l-50.07-50.06a88.11,88.11,0,1,0-11.31,11.31l50.06,50.07a8,8,0,0,0,11.32-11.32ZM40,112a72,72,0,1,1,72,72A72.08,72.08,0,0,1,40,112Z" />
    </svg>
  );

  const BookmarkIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="20px" height="20px" fill="currentColor" viewBox="0 0 256 256">
      <path d="M184,32H72A16,16,0,0,0,56,48V224a8,8,0,0,0,12.24,6.78L128,193.43l59.77,37.35A8,8,0,0,0,200,224V48A16,16,0,0,0,184,32Zm0,177.57-51.77-32.35a8,8,0,0,0-8.48,0L72,209.57V48H184Z" />
    </svg>
  );

  const CaretLeftIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="18px" height="18px" fill="currentColor" viewBox="0 0 256 256">
      <path d="M165.66,202.34a8,8,0,0,1-11.32,11.32l-80-80a8,8,0,0,1,0-11.32l80-80a8,8,0,0,1,11.32,11.32L91.31,128Z" />
    </svg>
  );

  const CaretRightIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="18px" height="18px" fill="currentColor" viewBox="0 0 256 256">
      <path d="M181.66,133.66l-80,80a8,8,0,0,1-11.32-11.32L164.69,128,90.34,53.66a8,8,0,0,1,11.32-11.32l80,80A8,8,0,0,1,181.66,133.66Z" />
    </svg>
  );

  const XIcon = () => (
    <svg xmlns="http://www.w3.org/2000/svg" width="24px" height="24px" fill="currentColor" viewBox="0 0 256 256">
      <path d="M205.66,194.34a8,8,0,0,1-11.32,11.32L128,139.31,61.66,205.66a8,8,0,0,1-11.32-11.32L116.69,128,50.34,61.66A8,8,0,0,1,61.66,50.34L128,116.69l66.34-66.35a8,8,0,0,1,11.32,11.32L139.31,128Z"/>
    </svg>
  );

  const FlagIcon = () => (
    <svg viewBox="0 0 48 48" fill="none" xmlns="http://www.w3.org/2000/svg" className="w-4 h-4">
      <path
        d="M4 42.4379C4 42.4379 14.0962 36.0744 24 41.1692C35.0664 46.8624 44 42.2078 44 42.2078L44 7.01134C44 7.01134 35.068 11.6577 24.0031 5.96913C14.0971 0.876274 4 7.27094 4 7.27094L4 42.4379Z"
        fill="currentColor"
      />
    </svg>
  );

  interface Story {
    id: number;
    title: string;
    description: string;
    image: string;
    genre: string;
    story: string;
  }

  const openModal = (story: Story) => {
    setSelectedStory(story);
  };

  const closeModal = () => {
    setSelectedStory(null);
  };

  return (
   <div className="relative flex size-full min-h-screen flex-col bg-gradient-to-br from-slate-950 via-indigo-950 to-blue-950 text-white" style={{ fontFamily: '"Noto Serif", "Noto Sans", sans-serif' }}>

      <div className="layout-container flex h-full grow flex-col">
        

        {/* Main Content */}
        <div className="px-40 flex flex-1 justify-center py-5">
          <div className="layout-content-container flex flex-col max-w-[960px] flex-1">
            {/* Search Bar */}
            <div className="px-4 py-3">
              <label className="flex flex-col min-w-40 h-12 w-full">
                <div className="flex w-full flex-1 items-stretch rounded-xl h-full border border-white shadow-[0_0_10px_white] transition-shadow duration-300">
                  <div className="text-white-300 flex border-none bg-slate-800/60 items-center justify-center pl-4 rounded-l-xl border-r-0">
                    <MagnifyingGlassIcon />
                  </div>
                  <input
                    placeholder="Search for stories"
                    className="form-input flex w-full min-w-0 flex-1 resize-none overflow-hidden rounded-xl text-white focus:outline-0 focus:ring-0 border-none bg-slate-800/60 focus:border-none h-full placeholder:text-white-300 px-4 rounded-l-none border-l-0 pl-2 text-base font-normal leading-normal"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                  />
                </div>
              </label>
            </div>

            {/* Genre Filter */}
            <h2 className="text-white text-[22px] font-bold leading-tight tracking-[-0.015em] px-4 pb-3 pt-5">Filter by Genre</h2>
            <div className="flex gap-3 p-3 flex-wrap pr-4">
              <div 
                className={`flex h-8 shrink-0 items-center justify-center gap-x-2 rounded-full pl-4 pr-4 cursor-pointer transition-colors ${
                  selectedGenre === '' ? 'bg-blue-500 text-white' : 'bg-slate-800/60 hover:bg-slate-700/60'
                }`}
                onClick={() => setSelectedGenre('')}
              >
                <p className="text-sm font-medium leading-normal">All</p>
              </div>
              {genres.map((genre) => (
                <div
                  key={genre}
                  className={`flex h-8 shrink-0 items-center justify-center gap-x-2 rounded-full pl-4 pr-4 cursor-pointer transition-colors ${
                    selectedGenre === genre ? 'bg-blue-500 text-white' : 'bg-slate-800/60 hover:bg-slate-700/60'
                  }`}
                  onClick={() => setSelectedGenre(genre)}
                >
                  <p className="text-sm font-medium leading-normal">{genre}</p>
                </div>
              ))}
            </div>

            {/* Stories Grid */}
            <div className="grid grid-cols-[repeat(auto-fit,minmax(200px,1fr))] gap-4 p-4">
              {displayedStories.map((story) => (
                <div key={story.id} className="flex flex-col gap-3 pb-3 group cursor-pointer" onClick={() => openModal(story)}>
                  <div
                    className="w-full bg-center bg-no-repeat aspect-video bg-cover rounded-xl group-hover:scale-105 transition-transform duration-300 shadow-lg"
                    style={{ backgroundImage: `url("${story.image}")` }}
                  />
                  <div>
                    <p className="text-white text-base font-medium leading-normal group-hover:text-blue-300 transition-colors">{story.title}</p>
                    <p className="text-white-300 text-sm font-normal leading-normal">{story.description}</p>
                    <p className="text-white-400 text-xs font-normal leading-normal mt-1">{story.genre}</p>
                  </div>
                </div>
              ))}
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="flex items-center justify-center p-4">
                <button
                  onClick={() => setCurrentPage(Math.max(1, currentPage - 1))}
                  disabled={currentPage === 1}
                  className="flex size-10 items-center justify-center hover:bg-slate-800/60 rounded-full transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <CaretLeftIcon />
                </button>
                
                {[...Array(totalPages)].map((_, index) => {
                  const pageNum = index + 1;
                  return (
                    <button
                      key={pageNum}
                      onClick={() => setCurrentPage(pageNum)}
                      className={`text-sm font-${currentPage === pageNum ? 'bold' : 'normal'} leading-normal flex size-10 items-center justify-center rounded-full transition-colors ${
                        currentPage === pageNum 
                          ? 'bg-blue-500 text-white' 
                          : 'text-white hover:bg-slate-800/60'
                      }`}
                    >
                      {pageNum}
                    </button>
                  );
                })}
                
                <button
                  onClick={() => setCurrentPage(Math.min(totalPages, currentPage + 1))}
                  disabled={currentPage === totalPages}
                  className="flex size-10 items-center justify-center hover:bg-slate-800/60 rounded-full transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
                >
                  <CaretRightIcon />
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Modal */}
      {selectedStory && (
        <div className="fixed inset-0 bg-black/80 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="bg-gradient-to-br from-slate-800 to-slate-900 rounded-2xl max-w-2xl w-full max-h-[80vh] overflow-y-auto border border-blue-500/20">
            <div className="p-6">
              <div className="flex justify-between items-start mb-4">
                <div>
                  <h2 className="text-2xl font-bold text-white mb-2">{selectedStory.title}</h2>
                  <span className="inline-block px-3 py-1 bg-blue-500/20 text-blue-300 rounded-full text-sm font-medium">
                    {selectedStory.genre}
                  </span>
                </div>
                <button
                  onClick={closeModal}
                  className="p-2 hover:bg-slate-700 rounded-full transition-colors"
                >
                  <XIcon />
                </button>
              </div>
              
              <div
                className="w-full h-64 bg-center bg-cover rounded-xl mb-6"
                style={{ backgroundImage: `url("${selectedStory.image}")` }}
              />
              
              <div className="space-y-4">
                <p className="text-blue-300 text-lg font-medium">{selectedStory.description}</p>
                <div className="prose prose-invert max-w-none">
                  <p className="text-gray-300 leading-relaxed text-base">
                    {selectedStory.story}
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Stories;